import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Search, Plus, Filter } from "lucide-react";

interface TaskFiltersProps {
  onSearch?: (query: string) => void;
  onFilterStatus?: (status: string) => void;
  onFilterPriority?: (priority: string) => void;
  onAddTask?: () => void;
}

export function TaskFilters({
  onSearch,
  onFilterStatus,
  onFilterPriority,
  onAddTask,
}: TaskFiltersProps) {
  return (
    <div className="flex flex-col sm:flex-row gap-4 items-start sm:items-center justify-between">
      <div className="relative flex-1 max-w-md">
        <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
        <Input
          placeholder="Search tasks..."
          className="pl-9"
          onChange={(e) => onSearch?.(e.target.value)}
          data-testid="input-search-tasks"
        />
      </div>

      <div className="flex gap-2 items-center flex-wrap">
        <Select onValueChange={onFilterStatus}>
          <SelectTrigger className="w-[140px]" data-testid="select-filter-status">
            <Filter className="h-4 w-4 mr-2" />
            <SelectValue placeholder="Status" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Status</SelectItem>
            <SelectItem value="todo">To Do</SelectItem>
            <SelectItem value="in-progress">In Progress</SelectItem>
            <SelectItem value="done">Done</SelectItem>
          </SelectContent>
        </Select>

        <Select onValueChange={onFilterPriority}>
          <SelectTrigger className="w-[140px]" data-testid="select-filter-priority">
            <Filter className="h-4 w-4 mr-2" />
            <SelectValue placeholder="Priority" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Priority</SelectItem>
            <SelectItem value="low">Low</SelectItem>
            <SelectItem value="medium">Medium</SelectItem>
            <SelectItem value="high">High</SelectItem>
          </SelectContent>
        </Select>

        <Button onClick={onAddTask} data-testid="button-add-task">
          <Plus className="h-4 w-4 mr-2" />
          Add Task
        </Button>
      </div>
    </div>
  );
}

client/src/pages/crm.tsx.content:
import { useState } from "react";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { DataTable, Column } from "@/components/data-table";
import { CRMTableFilters } from "@/components/crm-table-filters";
import { StatusBadge } from "@/components/status-badge";
import { UserAvatar } from "@/components/user-avatar";
import { LeadFormDialog } from "@/components/lead-form-dialog";
import { DealFormDialog } from "@/components/deal-form-dialog";
import { useQuery } from "@tanstack/react-query";
import type { Lead as LeadType, Deal as DealType, User } from "@shared/schema";
import { Skeleton } from "@/components/ui/skeleton";
import { formatDistanceToNow } from "date-fns";

type DisplayLead = {
  id: string;
  name: string;
  company: string;
  email: string;
  stage: "lead" | "negotiation" | "closed";
  assignedTo: string;
  lastContact: string;
};

type DisplayDeal = {
  id: string;
  title: string;
  company: string;
  value: number;
  stage: "lead" | "negotiation" | "closed";
  assignedTo: string;
  dueDate: string;
};

export default function CRM() {
  const [selectedTab, setSelectedTab] = useState("leads");
  const [leadDialogOpen, setLeadDialogOpen] = useState(false);
  const [dealDialogOpen, setDealDialogOpen] = useState(false);

  const { data: leads, isLoading: leadsLoading } = useQuery<LeadType[]>({
    queryKey: ["/api/leads"],
  });

  const { data: deals, isLoading: dealsLoading } = useQuery<DealType[]>({
    queryKey: ["/api/deals"],
  });

  const { data: users } = useQuery<User[]>({
    queryKey: ["/api/users"],
  });

  const getUserName = (userId: string | null) => {
    if (!userId || !users) return "Unassigned";
    const user = users.find(u => u.id === userId);
    return user ? `${user.firstName || ""} ${user.lastName || ""}`.trim() || user.email || "Unknown" : "Unknown";
  };

  const displayLeads: DisplayLead[] = leads?.map(lead => ({
    id: lead.id,
    name: lead.name,
    company: lead.company,
    email: lead.email,
    stage: lead.stage,
    assignedTo: getUserName(lead.assignedTo),
    lastContact: lead.lastContact 
      ? formatDistanceToNow(new Date(lead.lastContact), { addSuffix: true })
      : "Never",
  })) || [];

  const displayDeals: DisplayDeal[] = deals?.map(deal => ({
    id: deal.id,
    title: deal.title,
    company: deal.company,
    value: deal.value,
    stage: deal.stage,
    assignedTo: getUserName(deal.assignedTo),
    dueDate: deal.dueDate 
      ? formatDistanceToNow(new Date(deal.dueDate), { addSuffix: true })
      : "No due date",
  })) || [];

  const leadColumns: Column<DisplayLead>[] = [
    { header: "Name", accessorKey: "name" },
    { header: "Company", accessorKey: "company" },
    { header: "Email", accessorKey: "email" },
    {
      header: "Stage",
      accessorKey: "stage",
      cell: (value) => <StatusBadge status={value} />,
    },
    {
      header: "Assigned To",
      accessorKey: "assignedTo",
      cell: (value) => (
        <div className="flex items-center gap-2">
          <UserAvatar name={value} className="h-6 w-6" />
          <span className="text-sm">{value}</span>
        </div>
      ),
    },
    { header: "Last Contact", accessorKey: "lastContact" },
  ];

  const dealColumns: Column<DisplayDeal>[] = [
    { header: "Title", accessorKey: "title" },
    { header: "Company", accessorKey: "company" },
    { 
      header: "Value", 
      accessorKey: "value",
      cell: (value) => <span className="font-medium">${value.toLocaleString()}</span>,
    },
    {
      header: "Stage",
      accessorKey: "stage",
      cell: (value) => <StatusBadge status={value} />,
    },
    {
      header: "Assigned To",
      accessorKey: "assignedTo",
      cell: (value) => (
        <div className="flex items-center gap-2">
          <UserAvatar name={value} className="h-6 w-6" />
          <span className="text-sm">{value}</span>
        </div>
      ),
    },
    { header: "Due Date", accessorKey: "dueDate" },
  ];

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-semibold">CRM</h1>
        <p className="text-sm text-muted-foreground mt-1">Manage your leads, clients, deals, and contacts</p>
      </div>

      <LeadFormDialog open={leadDialogOpen} onOpenChange={setLeadDialogOpen} />
      <DealFormDialog open={dealDialogOpen} onOpenChange={setDealDialogOpen} />

      <Tabs value={selectedTab} onValueChange={setSelectedTab}>
        <TabsList>
          <TabsTrigger value="leads" data-testid="tab-leads">Leads</TabsTrigger>
          <TabsTrigger value="clients" data-testid="tab-clients">Clients</TabsTrigger>
          <TabsTrigger value="deals" data-testid="tab-deals">Deals</TabsTrigger>
          <TabsTrigger value="contacts" data-testid="tab-contacts">Contacts</TabsTrigger>
        </TabsList>

        <TabsContent value="leads" className="space-y-4 mt-6">
          <CRMTableFilters
            placeholder="Search leads..."
            addLabel="Add Lead"
            onSearch={(q) => console.log("Search:", q)}
            onFilterStage={(s) => console.log("Filter stage:", s)}
            onAdd={() => setLeadDialogOpen(true)}
            onExport={() => console.log("Export leads")}
          />
          {leadsLoading ? (
            <div className="space-y-3">
              <Skeleton className="h-12 w-full" />
              <Skeleton className="h-12 w-full" />
              <Skeleton className="h-12 w-full" />
            </div>
          ) : displayLeads.length === 0 ? (
            <div className="h-64 flex items-center justify-center text-muted-foreground">
              <p>No leads yet. Add your first lead to get started.</p>
            </div>
          ) : (
            <DataTable
              data={displayLeads}
              columns={leadColumns}
              onRowClick={(row) => console.log("Row clicked:", row)}
              actions={[
                { label: "Edit", onClick: (row) => console.log("Edit:", row) },
                { label: "Delete", onClick: (row) => console.log("Delete:", row) },
              ]}
              showSelection
              getRowId={(row) => row.id}
            />
          )}
        </TabsContent>

        <TabsContent value="clients" className="space-y-4 mt-6">
          <CRMTableFilters
            placeholder="Search clients..."
            addLabel="Add Client"
            onSearch={(q) => console.log("Search:", q)}
            onAdd={() => console.log("Add client")}
            onExport={() => console.log("Export clients")}
          />
          {leadsLoading ? (
            <div className="space-y-3">
              <Skeleton className="h-12 w-full" />
              <Skeleton className="h-12 w-full" />
            </div>
          ) : (
            <DataTable
              data={displayLeads.filter(l => l.stage === "closed")}
              columns={leadColumns}
              onRowClick={(row) => console.log("Row clicked:", row)}
              actions={[
                { label: "Edit", onClick: (row) => console.log("Edit:", row) },
                { label: "View Details", onClick: (row) => console.log("View:", row) },
              ]}
            />
          )}
        </TabsContent>

        <TabsContent value="deals" className="space-y-4 mt-6">
          <CRMTableFilters
            placeholder="Search deals..."
            addLabel="Add Deal"
            onSearch={(q) => console.log("Search:", q)}
            onFilterStage={(s) => console.log("Filter stage:", s)}
            onAdd={() => setDealDialogOpen(true)}
            onExport={() => console.log("Export deals")}
          />
          {dealsLoading ? (
            <div className="space-y-3">
              <Skeleton className="h-12 w-full" />
              <Skeleton className="h-12 w-full" />
              <Skeleton className="h-12 w-full" />
            </div>
          ) : displayDeals.length === 0 ? (
            <div className="h-64 flex items-center justify-center text-muted-foreground">
              <p>No deals yet. Add your first deal to get started.</p>
            </div>
          ) : (
            <DataTable
              data={displayDeals}
              columns={dealColumns}
              onRowClick={(row) => console.log("Row clicked:", row)}
              actions={[
                { label: "Edit", onClick: (row) => console.log("Edit:", row) },
                { label: "Delete", onClick: (row) => console.log("Delete:", row) },
              ]}
              showSelection
              getRowId={(row) => row.id}
            />
          )}
        </TabsContent>

        <TabsContent value="contacts" className="space-y-4 mt-6">
          <CRMTableFilters
            placeholder="Search contacts..."
            addLabel="Add Contact"
            onSearch={(q) => console.log("Search:", q)}
            onAdd={() => console.log("Add contact")}
            onExport={() => console.log("Export contacts")}
          />
          {leadsLoading ? (
            <div className="space-y-3">
              <Skeleton className="h-12 w-full" />
              <Skeleton className="h-12 w-full" />
            </div>
          ) : (
            <DataTable
              data={displayLeads}
              columns={leadColumns}
              onRowClick={(row) => console.log("Row clicked:", row)}
            />
          )}
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

