    url: "/social-profiles",
    icon: Share2,
  },
  {
    title: "Posting Schedule",
    url: "/posting-schedule",
    icon: Calendar,
  },
  {
    title: "Admin",
    url: "/admin",
    icon: UserCog,
  },
  {
    title: "Settings",
    url: "/settings",
    icon: Settings,
  },
];

export function AppSidebar() {
  const [location] = useLocation();
  const { user } = useAuth();

  const userName = user?.firstName && user?.lastName 
    ? `${user.firstName} ${user.lastName}`
    : user?.email || "User";

  return (
    <Sidebar>
