import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Clock, LogIn, LogOut } from "lucide-react";
import { format } from "date-fns";
import { Skeleton } from "@/components/ui/skeleton";
import type { Attendance } from "@shared/schema";

// Helper function to format timestamp in IST
const formatIST = (timestamp: Date | string | null) => {
  if (!timestamp) return "-";
  const date = new Date(timestamp);
  return date.toLocaleString('en-IN', {
    timeZone: 'Asia/Kolkata',
    year: 'numeric',
    month: 'short',
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
    hour12: true
  });
};

const formatDuration = (markIn: Date | string, markOut: Date | string | null) => {
  if (!markOut) return "In Progress";
  const start = new Date(markIn).getTime();
  const end = new Date(markOut).getTime();
  const diff = end - start;
  const hours = Math.floor(diff / (1000 * 60 * 60));
  const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
  return `${hours}h ${minutes}m`;
};

export default function Attendance() {
  const { toast } = useToast();

  const { data: attendance, isLoading } = useQuery<Attendance[]>({
    queryKey: ["/api/attendance/my"],
  });

  const openAttendance = attendance?.find(a => !a.markOutTime);

  const markInMutation = useMutation({
    mutationFn: async () => {
      return apiRequest("POST", "/api/attendance/mark-in", {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/attendance/my"] });
      queryClient.invalidateQueries({ queryKey: ["/api/attendance"] });
      toast({
        title: "Marked In",
        description: "You have successfully marked in for today.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const markOutMutation = useMutation({
    mutationFn: async (attendanceId: string) => {
      return apiRequest("PATCH", `/api/attendance/${attendanceId}/mark-out`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/attendance/my"] });
      queryClient.invalidateQueries({ queryKey: ["/api/attendance"] });
      toast({
        title: "Marked Out",
        description: "You have successfully marked out.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-semibold">Attendance</h1>
        <p className="text-sm text-muted-foreground mt-1">
          Track your daily attendance and work hours
        </p>
      </div>

      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Clock className="h-5 w-5" />
            Today's Attendance
          </CardTitle>
          <CardDescription>
            All times are shown in Indian Standard Time (IST)
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex gap-3">
            <Button
              onClick={() => markInMutation.mutate()}
              disabled={markInMutation.isPending || !!openAttendance}
              data-testid="button-mark-in"
            >
              <LogIn className="h-4 w-4 mr-2" />
              {markInMutation.isPending ? "Marking In..." : "Mark In"}
            </Button>

            <Button
              onClick={() => openAttendance && markOutMutation.mutate(openAttendance.id)}
              disabled={markOutMutation.isPending || !openAttendance}
              variant="outline"
              data-testid="button-mark-out"
            >
              <LogOut className="h-4 w-4 mr-2" />
              {markOutMutation.isPending ? "Marking Out..." : "Mark Out"}
            </Button>
          </div>

          {openAttendance && (
            <div className="p-4 bg-muted rounded-md">
              <p className="text-sm font-medium mb-1">Currently Marked In</p>
              <p className="text-sm text-muted-foreground">
                Marked in at: {formatIST(openAttendance.markInTime)}
              </p>
            </div>
          )}
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle>Attendance History</CardTitle>
          <CardDescription>Your recent attendance records</CardDescription>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="space-y-3">
              <Skeleton className="h-12 w-full" />
              <Skeleton className="h-12 w-full" />
              <Skeleton className="h-12 w-full" />
            </div>
          ) : !attendance || attendance.length === 0 ? (
            <div className="h-32 flex items-center justify-center text-muted-foreground">
              <p>No attendance records yet. Mark in to get started.</p>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead>
                  <tr className="border-b">
                    <th className="text-left py-3 px-4 text-sm font-medium text-muted-foreground">Date</th>
                    <th className="text-left py-3 px-4 text-sm font-medium text-muted-foreground">Mark In</th>
                    <th className="text-left py-3 px-4 text-sm font-medium text-muted-foreground">Mark Out</th>
                    <th className="text-left py-3 px-4 text-sm font-medium text-muted-foreground">Duration</th>
                  </tr>
                </thead>
                <tbody>
                  {attendance.map((record) => (
                    <tr key={record.id} className="border-b last:border-0" data-testid={`row-attendance-${record.id}`}>
                      <td className="py-3 px-4 text-sm">
                        {format(new Date(record.date), 'MMM d, yyyy')}
                      </td>
                      <td className="py-3 px-4 text-sm">
                        {formatIST(record.markInTime)}
                      </td>
                      <td className="py-3 px-4 text-sm">
                        {formatIST(record.markOutTime)}
                      </td>
                      <td className="py-3 px-4 text-sm">
                        {formatDuration(record.markInTime, record.markOutTime)}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/employee-form-dial.content:
import { useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQuery } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { insertEmployeeSchema, type InsertEmployee, type Employee, type User } from "@shared/schema";

interface EmployeeFormDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  data?: Employee;
}

export function EmployeeFormDialog({ open, onOpenChange, data }: EmployeeFormDialogProps) {
  const { toast } = useToast();
  const isEditing = !!data;

  interface EmployeeFormValues {
    userId?: string;
    department: string;
    phone?: string;
    performanceScore?: number;
  }

  const form = useForm<EmployeeFormValues>({
    resolver: zodResolver(insertEmployeeSchema),
    defaultValues: {
      userId: "",
      department: "",
      phone: "",
      performanceScore: 0,
    },
  });

  const { data: users } = useQuery<User[]>({
    queryKey: ["/api/users"],
  });

  useEffect(() => {
    if (data) {
      form.reset({
        userId: data.userId || "",
        department: data.department,
        phone: data.phone || "",
        performanceScore: data.performanceScore || 0,
      });
    } else {
      form.reset({
        userId: "",
        department: "",
        phone: "",
        performanceScore: 0,
      });
    }
  }, [data, form]);

  const mutation = useMutation({
    mutationFn: async (values: InsertEmployee) => {
      if (isEditing) {
        return apiRequest("PATCH", `/api/employees/${data.id}`, values);
      } else {
        return apiRequest("POST", "/api/employees", values);
      }
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/employees"] });
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      toast({
        title: isEditing ? "Employee updated" : "Employee created",
        description: isEditing ? "The employee has been updated successfully." : "The employee has been created successfully.",
      });
      onOpenChange(false);
      form.reset();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (values: InsertEmployee) => {
    mutation.mutate(values);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-2xl" data-testid="dialog-employee-form">
        <DialogHeader>
          <DialogTitle>{isEditing ? "Edit Employee" : "Add New Employee"}</DialogTitle>
        </DialogHeader>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            <FormField
              control={form.control}
              name="userId"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>User</FormLabel>
                  <Select onValueChange={field.onChange} value={field.value || undefined}>
                    <FormControl>
                      <SelectTrigger data-testid="select-employee-userId">
                        <SelectValue placeholder="Select user" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {users?.map((user) => (
                        <SelectItem key={user.id} value={user.id}>
                          {`${user.firstName || ""} ${user.lastName || ""}`.trim() || user.email || "Unknown"}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <FormField
                control={form.control}
                name="department"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Department</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-employee-department">
                          <SelectValue placeholder="Select department" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="Engineering">Engineering</SelectItem>
                        <SelectItem value="Product">Product</SelectItem>
                        <SelectItem value="Sales">Sales</SelectItem>
                        <SelectItem value="Marketing">Marketing</SelectItem>
                        <SelectItem value="Design">Design</SelectItem>
                        <SelectItem value="HR">HR</SelectItem>
                        <SelectItem value="Finance">Finance</SelectItem>
                        <SelectItem value="Operations">Operations</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="phone"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Phone</FormLabel>
                    <FormControl>
                      <Input placeholder="+1 (555) 000-0000" {...field} value={field.value || ""} data-testid="input-employee-phone" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="performanceScore"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Performance Score</FormLabel>
                  <FormControl>
                    <Input 
                      type="number" 
                      placeholder="85" 
                      {...field} 
                      value={field.value || 0}
                      onChange={(e) => field.onChange(parseInt(e.target.value) || 0)}
                      data-testid="input-employee-performanceScore" 
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <DialogFooter>
              <Button type="button" variant="outline" onClick={() => onOpenChange(false)} data-testid="button-cancel">
                Cancel
              </Button>
              <Button type="submit" disabled={mutation.isPending} data-testid="button-submit-employee">
                {mutation.isPending ? "Saving..." : isEditing ? "Update Employee" : "Create Employee"}
              </Button>
            </DialogFooter>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

